import React, { Component } from 'react';

import CounterControl from '../../components/CounterControl/CounterControl';
import CounterOutput from '../../components/CounterOutput/CounterOutput';
import {connect} from 'react-redux'
class Counter extends Component {
    state = {
        counter: 0
    }

    counterChangedHandler = ( action, value ) => {
        switch ( action ) {
            case 'inc':
                this.setState( ( prevState ) => { return { counter: prevState.counter + 1 } } )
                break;
            case 'dec':
                this.setState( ( prevState ) => { return { counter: prevState.counter - 1 } } )
                break;
            case 'add':
                this.setState( ( prevState ) => { return { counter: prevState.counter + value } } )
                break;
            case 'sub':
                this.setState( ( prevState ) => { return { counter: prevState.counter - value } } )
                break;
        }
    }

    render () {
        return (
            <div>
                <CounterOutput value={this.props.ctr} />
                <CounterControl label="Increment" clicked={this.props.onIncrementCounter} />
                <CounterControl label="Decrement" clicked={this.props.onDecrementCounter}  />
                <CounterControl label="Add 10" clicked={this.props.onAdd5Counter}  />
                <CounterControl label="Subtract 15" clicked={this.props.onSub5Counter}  />
                <hr />
                <button onClick={this.props.onStoreResult}>Store Result</button>
                <ul>
                    <li onclick={this.props.onDeleteResult}></li>
                </ul>
            </div>
        );
    }
}

const mapStateToProps= state =>{
    return{
        ctr:state.counter,
        storedResults:state.result
    }
}

const mapDisptchToProps = dispatch =>{
    return {
        onIncrementCounter:() =>dispatch({type:'INCREMENT'}),
        onDecrementCounter:() =>dispatch({type:'DECREMENT'}),
        onAdd5Counter:() =>dispatch({type:'ADD',val:10}),
        onSub5Counter:() =>dispatch({type:'SUB', val:15}),
        onStoreResult:() =>dispatch({type:'STORE_RESULT'}),
        onDeleteResult:() =>dispatch({type:'DELETE_RESULT'})
    }
}

export default connect(mapStateToProps,mapDisptchToProps)(Counter);
