const initialState = {
    counter:0,
    result:[]
}

const reducer = (state= initialState,action) =>{
    switch(action.type){
        case 'INCREMENT':
                const newwState = Object.assign({}, state);
                newwState.counter=state.counter +1;
                return newwState

        case 'DECREMENT':
                return{
                    ...state,
                    counter:state.counter - 1
                }
        case 'ADD':
                return{
                    counter:state.counter + action.val
                }
        case 'SUB':
                return{
                    counter:state.counter - action.val
                }
    }
    return state;
}

export default reducer
